  let url = base.replace("&start=0","&start="+start);
  let data = JSON.parse(java.fetch(url,{method: "GET",headers:{"Content-Type":"application/x-www-form-urlencoded","User-Agent":"kidstone.cn/3.9.4"}}).body().string()).data;
  if (data.end=="0") return;
  count++;
