function addZero(str){while(3-str.toString().length) str="0"+str;return str;}
var s1=bid%1000;
var s2=((bid-s1)/1000)%1000;
var s3=(bid-s1-s2*1000)/1000000;
"http://image.zymkcdn.com/file/cover/"+addZero(s3)+"/"+addZero(s2)+"/"+addZero(s1)+".jpg"

